const container=document.getElementById("container");function makeRows(e,t){for(container.style.setProperty("--grid-rows",e),container.style.setProperty("--grid-cols",t),c=0;c<e*t;++c){let t=document.createElement("div");container.appendChild(t).className="grid-item",t.addEventListener("mouseover",()=>{var e="rgb("+Math.floor(255*Math.random()).toString()+","+Math.floor(255*Math.random()).toString()+","+Math.floor(255*Math.random()).toString()+")";t.style.backgroundColor=e})}}function clearRows(e){for(;e.firstChild;)e.removeChild(e.firstChild)}makeRows(10,10);let sz=document.getElementById("size");sz.addEventListener("click",()=>{var e=parseInt(prompt("Please enter new size(1-120)","10"));0<e&&e<121?(clearRows(container),makeRows(e,e)):alert("Size not valid!!!")});let clear=document.getElementById("clear");clear.addEventListener("click",()=>{var e=Math.sqrt(container.childElementCount);clearRows(container),makeRows(e,e)});let toggleGrid=document.getElementById("toggle-grid");toggleGrid.addEventListener("click",()=>{"0px"!=container.style.getPropertyValue("--grid-size")?container.style.setProperty("--grid-size","0px"):container.style.setProperty("--grid-size","1px")});